(function($){$.fn.placeholder=function(options){return this.each(function(){var $this=$(this);var placeholder=$this.attr("placeholder");var clearOnFocus=options&&options["clearOnFocus"]||false;if(placeholder!=undefined&&placeholder!=""){$this.removeAttr("placeholder");var inputHolder=$this.wrap($(document.createElement("span")).addClass("input-holder")).parent();var inputDefault=$(document.createElement("span")).text(placeholder).addClass("input-default");inputHolder.append(inputDefault);inputDefault.click(function(){$this.focus()});
if(clearOnFocus){$this.focus(function(event){inputDefault.hide()});$this.blur(function(event){if($this.val().length==0)inputDefault.show()})}else{$this.keydown(function(event){if(event.keyCode!="8"&&event.keyCode!="9"&&event.keyCode!="16")inputDefault.hide()});$this.keyup(function(event){if($this.val().length==0)inputDefault.show()})}}})}})(jQuery);